import React, { useEffect, useState } from 'react'
import jwt from 'jsonwebtoken'
import { useHistory } from 'react-router-dom'

const Dashboard = () => {
	const history = useHistory()

	const logout=()=>{
        window.localStorage.clear();
        window.location.href='/studentlogin'
    }

	const [data, setData]= useState([])
    const item=[];


	useEffect(() => {
		const token = localStorage.getItem('token')
		if (token) {
			const user = jwt.decode(token)
			if (!user) {
				localStorage.removeItem('token')
				history.replace('/login')
			} 
			apiGet(user)
		}
		else{
			history.push('/login')
		}
	}, [])

	const apiGet = ()=>{
		const token = localStorage.getItem('token')
		const user = jwt.decode(token)
		const name="";
		fetch('http://localhost:1337/api/studentlogin')
        .then((response)=>response.json())
        .then((json)=>{
            setData(json);
            for(let i=0;i<json.user.length;i=i+1){
                if(user.email==json.user[i].email){
					name=json.user[i].name;
					console.log(user.email);
				}
            }
		})
    }


	return (
		<div>
			<h1>This is the student Dashboard</h1>
			<p>{JSON.stringify(data.user,null,2)}</p>

			<button onClick={logout}>Logout</button>
		</div>
	)
}

export default Dashboard
