import React from "react";
import Header from "../components/Header"
import '../index.css'



function FrontPage(){
    const registrationPage = () =>{
		window.location.href='/register'
	}

	const loginPage = () =>{
		window.location.href='/login'
	}

	const studentRegistration = () =>{
		window.location.href='/studentregistration'
	}

	const studentLogin = () =>{
		window.location.href='/studentlogin'
	}

    return(
        <div>
		<Header />
			<div class="front">
            <h1>Welcome!</h1>
			<button class="btn btn-light" onClick={registrationPage}>Staff Register</button>
			<button class="btn btn-dark" onClick={studentRegistration}>Student Register</button>
			<button class="btn btn-light" onClick={loginPage}>Staff Login</button>
			<button class="btn btn-dark" onClick={studentLogin}>Student Login</button>
			</div>
        </div>


    )
}

export default FrontPage
