import logo from './logo.svg';
import './App.css';
import {useState} from 'react';

function App() {
  const [name, setName]=useState('')
  const [email, setEmail]=useState('')
  const [password, setPassword]=useState('')
  //We've installed CORS package in the server side to overcome the error we get while feetching...the CORS policy error
  async function registerUser(event){
    event.preventDefault()//This will refresh the form after entering the data
    const response= await fetch('http://localhost:8080//api/register',{
      method:'POST',
      headers:{
        'Content-Type':'application/json',
      },
      body: JSON.stringify({
        name,
        email,
        password,
      })
    })

    const data=await response.json()

    console.log(data)

  }

  return <div>
    <h1>Registration</h1>
    <form onSubmit={registerUser}>
      <input value={name} onChange={(e) => setName(e.target.value)} type="text" placeholder="Name"></input>
      <input value={email} onChange={(e) => setEmail(e.target.value)} type="text" placeholder="email"></input>
      <input value={password} onChange={(e) => setPassword(e.target.value)} type="password" placeholder="password"></input>
      <input type="submit" value="Register"></input>
    </form>
  </div>

}

export default App;
