import React from "react";

class NoticePublish extends React.Component {

	// Constructor
	constructor(props) {
		super(props);

		this.state = {
			user: [],
			DataisLoaded: false
		};
	}

	// ComponentDidMount is used to
	// execute the code
	componentDidMount() {
		fetch("http://localhost:1337/api/notice")
			.then((res) => res.json())
			.then((json) => {
				this.setState({
					user: json,
					DataisLoaded: true
				});
			})
	}
	render() {
		const { DataisLoaded, user } = this.state;
		if (!DataisLoaded) return <div>
			<h1> Pleses wait some time.... </h1> </div> ;

		return (
		<div>
			<h1> Fetch data from an api in react </h1> {
				user.map((user) => (
				<ol>
					User_Name: { user },
					Full_Name: { user.notice }
					</ol>
				))
			}
		</div>
	);
}
}

export default NoticePublish;
