import React from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

const item=[];


export default class Fetch extends React.Component{

    state={
        loading: true,
        person: null,
    }

    async onDelete(nid) {
        const url="http://localhost:1337/api/notice";
        const response=await fetch(url);
        const data=await response.json();
        for(let i=data.user.length-1;i>=0;i--){
            if(data.user[i].nid==nid){
                data.delete({nid})
            }
        }
    }


    async componentDidMount(){
        const url="http://localhost:1337/api/notice";
        const response = await fetch(url);
        const data = await response.json();
        for(let i=data.user.length-1;i>=0;i--){
            this.setState({person: data, loading: false})
            item.push(data.user[i]);
            console.log(data.user[0])
        }

    }



    render(){
        return <div>
            {this.state.loading || !this.state.person ? 
            <div>loading...</div> 
            :
            <div>{item.map(i => 
            <div>
                <h1>NOTICE For Staffss</h1>
                <h2>{i.notice}</h2>
                <p>Undersigned: {i.name}</p>
                <button onClick={()=>this.onDelete(i.nid)}>Click</button>
            </div>
            )}</div>

            }

        </div>
    }
}
