import React from "react";
import { useState } from 'react'
import { useHistory } from 'react-router-dom'
//name phone address roll registration email password
function RoomAllotment() {
	const history = useHistory()

	const [room, setRoom] = useState('') //the first value is current state and second value is function used to update our state
	const [name, setName] = useState('')
    const [block, setBlock] = useState('')
    const [phone, setPhone] = useState('')
	const [roll, setRoll] = useState('')

	async function registerUser(event) {
		event.preventDefault()

		const response = await fetch('http://localhost:1337/api/allot', {
			method: 'POST',
			headers: {
				'Content-Type': 'application/json',
			},
			body: JSON.stringify({
				room,
                name,
                block,
                phone,
				roll
			}),
		})

		const data = await response.json()

		if (data.status === 'ok') {
			window.location.reload() // if registration is successfull 
		}
	}


	return (
		<div>
			<h1>Allot Rooms</h1>
			<form onSubmit={registerUser}>
				<input
					value={room}
					onChange={(e) => setRoom(e.target.value)}
					type="number"
					placeholder="Room"
				/>
				<br />
                <input
					value={name}
					onChange={(e) => setName(e.target.value)}
					type="text"
					placeholder="Name"
				/>
				<br />
                <input
					value={block}
					onChange={(e) => setBlock(e.target.value)}
					type="text"
					placeholder="Block"
				/>
				<br />
                <input
					value={phone}
					onChange={(e) => setPhone(e.target.value)}
					type="number"
					placeholder="Phone"
				/>
				<br />
				<input
					value={roll}
					onChange={(e) => setRoll(e.target.value)}
					type="number"
					placeholder="Roll"
				/>
				<br />
				<input type="submit" value="Allot Room"/>
			</form>
		</div>
	)
}

export default RoomAllotment
