import { useState } from 'react'
import { useHistory } from 'react-router-dom'
//name phone address roll registration email password
function StudentRegistration() {
	const history = useHistory()

	const [name, setName] = useState('') //the first value is current state and second value is function used to update our state
	const [phone, setPhone] = useState('')
    const [address, setAddress] = useState('')
    const [roll, setRoll] = useState('')
    const [registration, setRegistration] = useState('')
    const [email, setEmail] = useState('')
	const [password, setPassword] = useState('')


  const back=()=>{
	  window.location.href='/adminlogin';
  }

	async function registerUser(event) {
		event.preventDefault()

		const response = await fetch('http://localhost:1337/api/studentregister', {
			method: 'POST',
			headers: {
				'Content-Type': 'application/json',
			},
			body: JSON.stringify({
				name,
                phone,
                address,
                roll,
                registration,
				email,
				password,
			}),
		})

		const data = await response.json()

		if (data.status === 'ok') {
			history.push('/login') // if registration is successfull 
		}
	}

	return (
		<div>
			<h1>Student Registration</h1>
			<form onSubmit={registerUser}>
				<input
					value={name}
					onChange={(e) => setName(e.target.value)}
					type="text"
					placeholder="Name"
					class="input-control"
				/>
				<br />
                <input
					value={phone}
					onChange={(e) => setPhone(e.target.value)}
					type="number"
					placeholder="Phone"
					class="input-control"
				/>
				<br />
                <input
					value={address}
					onChange={(e) => setAddress(e.target.value)}
					type="text"
					placeholder="Address"
					class="input-control"
				/>
				<br />
                <input
					value={roll}
					onChange={(e) => setRoll(e.target.value)}
					type="text"
					placeholder="Roll"
					class="input-control"
				/>
				<br />
                <input
					value={registration}
					onChange={(e) => setRegistration(e.target.value)}
					type="text"
					placeholder="Registration Number"
					class="input-control"
				/>
				<br />
				<input
					value={email}
					onChange={(e) => setEmail(e.target.value)}
					type="email"
					placeholder="Email"
					class="input-control"
				/>
				<br />
				<input
					value={password}
					onChange={(e) => setPassword(e.target.value)}
					type="password"
					placeholder="Password"
					class="input-control"
				/>
				<br />
				<input class="input-control" type="submit" value="Register" />
			</form>
			<button onClick={back}>Back</button>
		</div>
	)
}

export default StudentRegistration
