import React, {useEffect,useState} from 'react'

function Notice () {

    const [name,setName] = useState('')
    const [notice,setNotice] = useState('')

    const noticePage = () =>{
        window.location.href='/notice';
    }

    //Publish Notice
    async function populateQuote() {
		const req = await fetch('http://localhost:1337/api/notice')

		const data = await req.json()
		if (data.status === 'ok') {
            const notice=data;
			console.warn(data)
            return notice
		} else {
            console.warn(data)
		}
	}

    async function publishNotice(event){
        event.preventDefault()
        const response = await fetch('http://localhost:1337/api/notice',{
            method: 'POST',
            headers: {
                'Content-Type':'application/json',
            },
            body: JSON.stringify({
                name,
                notice,
                notices:[]
            }),
        })
        const data=await response.json()
        if (data.status == 'ok') {
            alert('Notice Published')
		}
    }

    //Updates checking

    useEffect(() => {
	populateQuote()
	}, [])

    //Display them

    return(
        <div>
            <h1>HOSTEL WARD</h1>

            <h2>{notice}</h2>
            <form onSubmit={publishNotice}>
                <input
					value={name}
                    onChange={(e) => setName(e.target.value)}
                    type="text"
                    placeholder="name"
				/>
				<br />
				<input
					value={notice}
                    onChange ={(e) => setNotice(e.target.value)}
                    type="text"
                    placeholder="notice"
				/>
				<br />
				<input type="submit" value="publishDone" onClick={{noticePage}}/>
			</form>
            <div className='blog'>
                {this.displayNotice(this.state.notices)}
            </div>
        </div>
    )
}

export default Notice
