import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';

const item=[];
const searchData=[];
const email='';

export default class studentDetails extends React.Component{

    constructor(props){
        super(props);
        this.state={
            inputValue:'',
        };
    }

    state={
        loading: true,
        person: null,
    }



    async componentDidMount(x){
        const url="http://localhost:1337/api/studentlogin";
        const response = await fetch(url);
        const data = await response.json();
        if(x!=null){
            for(let i=0;i<data.user.length;i++){
                if(data.user[i].roll==x || data.user[i].name==x){
                    searchData.push(item[i].email);
                    searchData.push(item[i].address);
                    searchData.push(item[i].roll);
                    searchData.push(item[i].name);
                    console.log(searchData[0]);
                }
            }
        }
        for(let i=data.user.length-1;i>=0;i--){
            this.setState({person: data, loading: false})
            item.push(data.user[i]);
            console.log(data.user[0])
        }


    }

    updateInputValue(evt) {
        this.setState({
          inputValue: evt.target.value
        });
      }

    displaySearch(x,item){
        for(let i=0;i<item.length;i++){
            if(item[i].roll==x || item[i].name==x){
                searchData.push(item[i].email);
                searchData.push(item[i].address);
                searchData.push(item[i].roll);
                searchData.push(item[i].name);
                console.log(searchData[0]);
            }
        }
    }


    render(){

        return <div>
            <input id="number" type="text" 
            onChange={(evt) => {var x=evt.target.value; this.componentDidMount(x) }} />

            <div>{searchData.map(student => 
                <div>

                    <h1>Student</h1>
                    <p>E-mail ID: {student}</p>

                </div>
            )}</div>

            {this.state.loading || !this.state.person ? 
            <div>loading...</div> 
            :
            <div>{item.map(student => 
            <div>

                <h1>Student</h1>
                <h2>{student.title}</h2>
                <p>Name: {student.name}</p>
                <p>Roll No.: {student.roll}</p>
                <p>Registration No.: {student.registration}</p>
                <p>Address: {student.address}</p>
                <p>Phone Number: {student.phone}</p>
                <p>E-mail ID: {student.email}</p>

            </div>
            )}</div>

            }

        </div>
    }
}
