import React from 'react';
import { useHistory } from 'react-router-dom';
import noticeDisplay from '../noticeDisplay.css';
import Header from './Header.js'

const item=[];

export default class Fetch extends React.Component{

    state={
        loading: true,
        person: null,
    }

    async componentDidMount(){
        const url="http://localhost:1337/api/notice";
        const response = await fetch(url);
        const data = await response.json();
        for(let i=data.user.length-1;i>=-1;i--){
            this.setState({person: data, loading: false})
            item.push(data.user[i]);
            console.log(data.user[0])
        }

    }

    render(){
        return <div>
        <Header />
            <div class="outer">            <center><h1>NOTICE</h1></center>
            {item.map(i => 
            <div class="box">
                <h5>{i.noticetitle}</h5>
                <p>{i.notice}</p>
                <h6>Undersigned: <i>{i.name}</i></h6>
                <hr></hr>
            </div>

            )}</div>
        </div>
    }
}
