import React, {useEffect,useState} from 'react'
import jwt from 'jsonwebtoken'
import { useHistory } from 'react-router-dom'

function Notice () {

    const [name,setName] = useState('')
    const [notice,setNotice] = useState('')
    const [tempNotice, setTempNotice] = useState('')

    const history = useHistory()


    const noticePage = () =>{
        window.location.href='/notice';
    }

    async function populateNotice() {
		const req = await fetch('http://localhost:1337/api/notice', {
			headers: {
				'x-access-token': localStorage.getItem('token'),
			},
		})

		const data = await req.json()
		if (data.status === 'ok') {
			setNotice(data.quote)
		} else {
			alert(data.error)
		}
	}

    useEffect(() => {
		const token = localStorage.getItem('token')
		if (token) {
			const user = jwt.decode(token)

				populateNotice()
		}
	}, [])

    async function publishNotice(event){
        event.preventDefault()
        const response = await fetch('http://localhost:1337/api/notice',{
            method: 'POST',
            headers: {
                'Content-Type':'application/json',
                'x-access-token': localStorage.getItem('token'),
            },
            body: JSON.stringify({
                name,
                notice,
            }),
        })
        const data=await response.json()
        if (data.status === 'ok') {
            setNotice(notice)
            setNotice('')
            alert('Notice Published')
		}
    }

    return(
        <div>
            <h1>Your quote: {notice || 'No quote found'}</h1>
            <h1>HOSTEL WARD</h1>
            <form onSubmit={publishNotice}>
                <input
					value={name}
                    onChange={(e) => setName(e.target.value)}
                    type="text"
                    placeholder="name"
				/>
				<br />
				<input
					value={notice}
                    onChange ={(e) => setNotice(e.target.value)}
                    type="text"
                    placeholder="notice"
				/>
				<br />
				<input type="submit" value="publish" />
			</form>
      <button onClick={noticePage}>Publish</button>
        </div>
    )
}

export default Notice
