import { useState } from 'react'
import { useHistory } from 'react-router-dom'

function App() {
	const history = useHistory()

	const [name, setName] = useState('') //the first value is current state and second value is function used to update our state
	const [email, setEmail] = useState('')
	const [password, setPassword] = useState('')
	const [notice,setNotice] = useState('')

  const login=()=>{
    window.location.href='/login';
  }

  	async function populateNotice(){
		  const req=await fetch('http://localhost:3000/api/notice',{
			body: JSON.stringify({
				notice: notice,
			})
		  })
		  const data=await req.json()
		  if(data.status =='ok'){
			setNotice(data.notice)
		  }
		  else{
			  alert(data.error)
		  }
	  }
	async function registerUser(event) {
		event.preventDefault()

		const response = await fetch('http://localhost:1337/api/register', {
			method: 'POST',
			headers: {
				'Content-Type': 'application/json',
			},
			body: JSON.stringify({
				name,
				email,
				password,
			}),
		})

		const data = await response.json()

		if (data.status === 'ok') {
			history.push('/login') // if registration is successfull 
		}
	}

	return (
		<div>
			<h1>Register</h1>
			<h2>Notice:: {notice || 'No notice found'}</h2>
			<form onSubmit={registerUser}>
				<input
					value={name}
					onChange={(e) => setName(e.target.value)}
					type="text"
					placeholder="Name"
				/>
				<br />
				<input
					value={email}
					onChange={(e) => setEmail(e.target.value)}
					type="email"
					placeholder="Email"
				/>
				<br />
				<input
					value={password}
					onChange={(e) => setPassword(e.target.value)}
					type="password"
					placeholder="Password"
				/>
				<br />
				<input type="submit" value="Register" />
			</form>
      <button onClick={login}>Login</button>
		</div>
	)
}

export default App
